// Placeholder flatpickr loader.
// Replace this file with an actual copy of flatpickr.min.js for full offline hosting.
// This stub will lazy-load the CDN version so the UI still works if you haven't vendored the library.
(function(){
    if (typeof window.flatpickr === 'function') return; // already present
    var script = document.createElement('script');
    script.src = 'https://cdn.jsdelivr.net/npm/flatpickr';
    script.defer = true;
    script.onload = function(){ console.info('flatpickr loaded from CDN (placeholder)'); };
    script.onerror = function(){ console.warn('Failed to load flatpickr from CDN'); };
    document.head.appendChild(script);
})();
